import React, { Fragment } from "react";

class QuoteBox extends React.Component {
  constructor() {
    super();
    this.state = {
      quotes: [],
      quoteIndex: null,
    };
  }

  componentDidMount() {
    fetch(
      "https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json"
    )
      .then((data) => data.json())
      .then((quotes) =>
        this.setState({ quotes }, () => {
          this.setState({ quoteIndex: this.getRandomQuote() });
        })
      );
  }

  getRandomQuote = () => {
    const randomIndex = Math.floor(Math.random() * this.state.quotes.length);
    console.log(this.state.quotes[this.randomIndex]);
    return this.state.quotes[randomIndex];
  };

  shuffleQuotes(array) {
    return array.sort(() => Math.random() - 0.5);
  }

  handleClick = () => {
    const generateRandomQuote = this.get();
    this.setState({
      quote: generateRandomQuote.quote,
      author: generateRandomQuote.author,
    });
    this.shuffleQuotes(this.state.quotes);
  };

  render() {
    return (
      <Fragment>
        {/*A common pattern in React is for a component to return multiple elements. 
        Fragments let you group a list of children without adding extra nodes to the DOM.*/}
        <h1>RandomQuoteGenerator</h1>
        <button handleClick={this.handleClick}>New Quote</button>
        {this.state}
        <br />
      </Fragment>
    );
  }
}

export default QuoteBox;
