import React, { Fragment } from "react";

class QuoteBox extends React.Component {
  constructor() {
    super();
    this.state = {
      quotes: [],
      quoteIndex: null
    };
  }

  componentDidMount() {
    fetch(
      "https://gist.githubusercontent.com/natebass/b0a548425a73bdf8ea5c618149fe1fce/raw/f4231cd5961f026264bb6bb3a6c41671b044f1f4/quotes.json"
    )
      .then(data => data.json())
      .then(quotes =>
        this.setState({ quotes }, () => {
          this.setState({ quoteIndex: this.getRandomQuote() });
        })
      );
  }

  getRandomQuote = () => {
    let data = res.data.quotes;
    let quoteNum = Math.floor(Math.random() * data.length); //quote number
    let randomQuote = data[quoteNum]; //actual quote

    this.setState({
      quote: randomQuote["quote"],
      author: randomQuote["author"]
    });
  };

  render() {
    return (
      <Fragment>
        {/*A common pattern in React is for a component to return multiple elements. 
        Fragments let you group a list of children without adding extra nodes to the DOM.*/}
        <h1>RandomQuoteGenerator</h1>
        <button onClick={this.getRandomQuote}>New Quote</button>
        <br />
        {this.hasQuote === true ? null : "no quote yet"}
      </Fragment>
    );
  }
}

export default QuoteBox;
